import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View } from 'react-native';
import { CheckBox, Text } from '@rneui/themed';
import { FlatList } from 'react-native'
import { TextInput, Button } from 'react-native';
import * as Font from 'expo-font'
import * as React from 'react'
import { NavigationContainer } from "@react-navigation/native"
import { createNativeStackNavigator } from "@react-navigation/native-stack"
import FontAwesome from '@expo/vector-icons/FontAwesome'
import { useState } from 'react'
import { Input, Button as RNButton, Text as RNText } from 'react-native-elements';
import AsyncStorage from '@react-native-async-storage/async-storage';


async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font))
}

let initTasks = [
  {description: "Task 1", completed: true, key: 1},
  {description: "Task 2", completed: true, key: 2}
]

const Stack = createNativeStackNavigator()

export default function App(){
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Login">
        <Stack.Screen name="Todo List" component={TodoScreen} />
        <Stack.Screen name="Details" component={DetailsScreen} />
        <Stack.Screen name="Login" component={LoginScreen} />
        <Stack.Screen name="Registration" component={RegistrationScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  )
}


function LoginScreen({ navigation }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = async () => {
    try {
      const users = await AsyncStorage.getItem('users');
      if (users) {
        const loginInfo = JSON.parse(users);
        const foundUser = loginInfo.find(user => user.username === username && user.password === password);
        if (foundUser) {
          navigation.navigate("Todo List");
          setError("");
        } else {
          setError("Incorrect username or password");
        }
      } else {
        setError("No registered users found. Please register first.");
      }
    } catch (error) {
      console.error("Error retrieving login data: ", error);
    }
  };

  return (
    <View style={styles.container}>
      <StatusBar style="auto" />
      <View style={styles.formContainer}>
        <Input
          placeholder="Login"
          leftIcon={{ type: 'font-awesome', name: 'user' }}
          onChangeText={setUsername}
          value={username}
          testID="login-username"
        />
        <Input
          placeholder="Password"
          leftIcon={{ type: 'font-awesome', name: 'lock' }}
          secureTextEntry={true}
          onChangeText={setPassword}
          value={password}
          testID="login-password"
        />
        <Button
          title="Login"
          containerStyle={styles.buttonContainer}
          buttonStyle={styles.loginButton}
          onPress={handleLogin}
          testID="login-button"
        />
        <Button
          title="Register"
          containerStyle={styles.buttonContainer}
          buttonStyle={styles.registerButton}
          onPress={() => navigation.navigate("Registration")}
          testID="login-register"
        />
        {error ? <Text style={styles.errorMessage}>{error}</Text> : null}
      </View>
    </View>
  );
}


function RegistrationScreen({ navigation }) {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [username, setUsername] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [email, setEmail] = useState("");
  const [zipCode, setZipCode] = useState("");
  const [newsletter, setNewsletter] = useState(false);
  const [error, setError] = useState("");
  const [isRegisterButtonDisabled, setIsRegisterButtonDisabled] = useState(true);

  const validateFields = () => {
    setIsRegisterButtonDisabled(
      !firstName || !lastName || !username || !phoneNumber || !password || !confirmPassword || !email || !zipCode
    );
  };


  const handleRegister = async () => {
    // Validate inputs
    if (!firstName || !lastName || !username || !phoneNumber || !password || !confirmPassword || !email || !zipCode) {
      setError("Please fill in all fields");
      return;
    }

    if (password !== confirmPassword) {
      setError("Passwords do not match");
      return;
    }

 // Retrieve existing user data or initialize it as an empty array
 let users = [];
 try {
   const existingData = await AsyncStorage.getItem('users');
   if (existingData) {
     users = JSON.parse(existingData);
   }
 } catch (error) {
   console.error("Error retrieving user data:", error);
 }

 // Check if the username is already taken
 const usernameExists = users.some(user => user.username === username);
 if (usernameExists) {
   setError("Username already exists. Please choose a different one.");
   return;
 }

 // Add the new user to the array
 const newUser = {
   firstName,
   lastName,
   username,
   phoneNumber,
   password,
   email,
   zipCode,
   newsletter,
 };
 users.push(newUser);

 // Save the updated user data back to AsyncStorage
 try {
   await AsyncStorage.setItem('users', JSON.stringify(users));
   navigation.navigate("Login");
 } catch (error) {
   console.error("Error saving user data:", error);
   setError("Error registering user");
 }
};


  return (
    <View style={styles.container}>
      <StatusBar style="auto" />
      <View style={styles.formContainer}>
        <TextInput
          onChangeText={text => { setFirstName(text); validateFields(); }}
          value={firstName}
          placeholder="First Name"
          testID="firstname"
        />
        <TextInput
          onChangeText={text => { setLastName(text); validateFields(); }}
          value={lastName}
          placeholder="Last Name"
          testID="lastname"
        />
        <TextInput
          onChangeText={text => { setUsername(text); validateFields(); }}
          value={username}
          placeholder="Username"
          testID="username"
        />
        <TextInput
          onChangeText={text => { setPhoneNumber(text); validateFields(); }}
          value={phoneNumber}
          placeholder="Phone Number"
          testID="phonenumber"
        />
        <TextInput
          onChangeText={text => { setPassword(text); validateFields(); }}
          value={password}
          placeholder="Password"
          secureTextEntry={true}
          testID="password"
        />
        <TextInput
          onChangeText={text => { setConfirmPassword(text); validateFields(); }}
          value={confirmPassword}
          placeholder="Confirm Password"
          secureTextEntry={true}
          testID="confirmpassword"
        />
        <TextInput
          onChangeText={text => { setEmail(text); validateFields(); }}
          value={email}
          placeholder="Email"
          testID="email"
        />
        <TextInput
          onChangeText={text => { setZipCode(text); validateFields(); }}
          value={zipCode}
          placeholder="ZIP Code"
          testID="zip"
        />
        <CheckBox
          title="Sign up for newsletter"
          checked={newsletter}
          onPress={() => setNewsletter(!newsletter)}
          testID="newsletter"
        />
        <Button
          title="Register"
          onPress={handleRegister}
          disabled={isRegisterButtonDisabled} // disable the button based on state
          buttonStyle={[styles.registerButton, isRegisterButtonDisabled && styles.disabledButton]} // apply styles conditionally
          testID="register-button"
        />
        {error ? <Text style={styles.errorMessage}>{error}</Text> : null}
      </View>
    </View>
  );
}


function DetailsScreen() {
  return (
    <View style={{ flex:1, alignItems: "center", justifyContent: "center" }}>
      <Text>Details Screen</Text>
      </View>
  )
}


function TodoScreen({navigation}) {
  cacheFonts([FontAwesome.font])
  let [tasks, setTasks] = useState(initTasks)
  let [input, setInput] = useState("")
  let updateTask = (task) => {
    console.log(task)
    task.completed = !task.completed
    setTasks([...tasks])
  }

  let addTask = () => {
    let maxKey = 0
    tasks.forEach(task => {
      if(task.key > maxKey) {
        maxKey = task.key
      }

    })
    setTasks([...tasks, {
      description: input,
      completed: false,
      key: maxKey+1
    }])
    console.log(tasks)
    setInput("")
  }

  let renderItem = ({item}) => {
    return (
      <View style ={styles.horizontal}>
    <CheckBox
      textStyle={item.completed ? {
        textDecorationLine: "line-through",
        textDecorationStyle: "solid",
      } : undefined}
      title={item.description}
      checked={item.completed}
      onPress={() => updateTask(item)}
      />
      <Button title ="Details" onPress={() => navigation.navigate("Details")}/>
      </View>
    );
  };

  return (
    <View style={[styles.container]}>
      <StatusBar style="auto" />
        <FlatList data={tasks} renderItem={renderItem} />
        <View style={[styles.horizontal]}>
      <TextInput
        onChangeText={setInput}
        value={input}
        placeholder="New task..."
      ></TextInput>< Button title="Add Task" onPress={addTask}/>
        </View>
    </View>
  );
}

const styles = StyleSheet.create({
  image: {
    flex: 1,
    aspectRatio: 1,
    width: '50%',
    backgroundColor: '#0553',
  },
  horizontal: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    padding: 10,
  },
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    alignItems: 'center',
    backgroundColor: '#DDDDDD',
    padding: 10,
  },
  formContainer: {
    width: '80%',
    alignItems: 'center',
  },
  buttonContainer: {
    width: '100%',
    marginVertical: 10,
  },
  loginButton: {
    backgroundColor: '#007bff',
  },
  registerButton: {
    backgroundColor: '#28a745',
  },
  errorMessage: {
    color: 'red',
    marginTop: 10,
  },

  registerButton: {
    backgroundColor: '#28a745',
  },
  disabledButton: {
    backgroundColor: '#ccc',
  },
});
