import React, { useState } from 'react-native';
import { View, Text, Button } from 'react-native';

export default function RepetitionExercise({ exercise, setMenuScreen }) {
  const [count, setCount] = useState(0);

  const incrementCount = () => {
    setCount(prevCount => prevCount + 1);
  };

  const decrementCount = () => {
    if (count > 0) {
      setCount(prevCount => prevCount - 1);
    }
  };

  const resetCount = () => {
    setCount(0);
  };

  return (
    <View>
      <Text>Repetition Exercise: {exercise}</Text>
      <Text>Count: {count}</Text>
      <Button title="Increment" onPress={incrementCount} />
      <Button title="Decrement" onPress={decrementCount} />
      <Button title="Reset" onPress={resetCount} />
      <Button title="Back to Menu" onPress={() => setMenuScreen(true)} />
    </View>
  );
}
