import React, { useState } from 'react';
import { View, Text, Button } from 'react-native'; // Import from react-native

export default function RepetitionExercise({ route, navigation }) {
  const { name } = route.params;
  const [count, setCount] = useState(0);

  const increaseCount = () => {
    setCount(count + 1);
  };

  const resetCount = () => {
    setCount(0);
  };

  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text>{name}</Text>
      <Text>Count: {count}</Text>
      <Button title="Increase Count" onPress={increaseCount} />
      <Button title="Reset Count" onPress={resetCount} />
      <Button title="Back to Home" onPress={() => navigation.navigate('Home')} />
    </View>
  );
}
