import React, { useState } from 'react';
import { View, Text, Button, StyleSheet } from 'react-native';

const DurationExercise = ({ navigation, route }) => {
  const [timer, setTimer] = useState(0);
  const [isActive, setIsActive] = useState(false);

  const toggleTimer = () => {
    setIsActive(!isActive);
  };

  const resetTimer = () => {
    setTimer(0);
    setIsActive(false);
  };

  const suggestedActivity = route.params && route.params.suggested ? route.params.suggested : "Unknown";
  const activity = route.params && route.params.activity ? route.params.activity : "Unknown"; // Ensure activity is defined
  console.log("Suggested Activity:", suggestedActivity); // Log suggested activity to check

  React.useEffect(() => {
    let interval = null;
    if (isActive) {
      interval = setInterval(() => {
        setTimer((timer) => timer + 1);
      }, 1000);
    } else if (!isActive && timer !== 0) {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isActive, timer]);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>{activity}</Text>
      <Text style={styles.timer}>{timer}</Text>
      <View style={styles.buttonContainer}>
        <Button title={isActive ? "Pause" : "Start"} onPress={toggleTimer} />
        <Button title="Reset" onPress={resetTimer} />
        <Button
          title={`Suggested: ${suggestedActivity}`}
          onPress={() => navigation.navigate(suggestedActivity)}
        />
      </View>
      <Button title="Home" onPress={() => navigation.navigate('Home')} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 24,
    marginBottom: 20,
  },
  timer: {
    fontSize: 48,
    marginBottom: 20,
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-evenly',
    width: '60%',
  },
});

export default DurationExercise;
