import React, { useState, useEffect } from 'react';
import { View, Text, Button, StyleSheet } from 'react-native';

const Stopwatch = ({ isRunning }) => {
  const [timer, setTimer] = useState(0);

  useEffect(() => {
    let interval;
    if (isRunning) {
      interval = setInterval(() => {
        setTimer((prevTimer) => prevTimer + 1); // Update timer every millisecond
      }, 1); // Change interval to 1 millisecond
    } else {
      clearInterval(interval);
    }

    return () => clearInterval(interval); // Cleanup interval on unmount or when isRunning changes
  }, [isRunning]);

  const formatTime = (time) => {
    const hours = Math.floor(time / (1000 * 60 * 60)).toString().padStart(2, '0');
    const minutes = Math.floor((time % (1000 * 60 * 60)) / (1000 * 60)).toString().padStart(2, '0');
    const seconds = Math.floor((time % (1000 * 60)) / 1000).toString().padStart(2, '0');
    const milliseconds = Math.floor(time % 1000).toString().padStart(3, '0');
    return `${hours}:${minutes}:${seconds}:${milliseconds}`;
  };



  return (
    <View style={styles.container}>
      <Text style={styles.timer}>{formatTime(timer)}</Text>
    </View>
  );
};

const DurationExercise = ({ navigation }) => {
  const [isRunning, setIsRunning] = useState(false);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Duration Exercise</Text>
      <Stopwatch isRunning={isRunning} />
      <View style={styles.buttonContainer}>
        <Button
          title={isRunning ? 'Stop' : 'Start'}
          onPress={() => setIsRunning(!isRunning)}
        />
        <Button
          title="Reset"
          onPress={() => setIsRunning(false)}
        />
        <Button
          title="Home"
          onPress={() => navigation.navigate('Home')}
        />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 24,
    marginBottom: 20,
  },
  timer: {
    fontSize: 48,
    marginBottom: 20,
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-evenly',
    marginTop: 20,
    width: '100%',
  },
});

export default DurationExercise;
