import React from 'react';
import { View, Text } from 'react-native';

const Stopwatch = ({ elapsedTime }) => {
  // Convert elapsed time to seconds and format it as HH:MM:SS
  const formatElapsedTime = (time) => {
    const hours = Math.floor(time / 3600000).toString().padStart(2, '0');
    const minutes = Math.floor((time % 3600000) / 60000).toString().padStart(2, '0');
    const seconds = Math.floor((time % 60000) / 1000).toString().padStart(2, '0');
    return `${hours}:${minutes}:${seconds}`;
  };

  return (
    <View>
      <Text>{formatElapsedTime(elapsedTime)}</Text>
    </View>
  );
};

export default Stopwatch;
